trie_index([A|C], D, B, E, F) :-
	trie_step(A, B, B, C, D, E, F).
trie_step([], _, _, [], [], A, A) :- !.
trie_step(C, K, L, A, J, M, N) :-
	A=[B|_],
	length(B, G),
	aggregate_all(set(E/F),
		      ( member(D, C),
			functor(D, E, F)
		      ),
		      H),
	I is G+1,
	trie_step_cases(H,
			I,
			C,
			A,
			J,
			K,
			L,
			M,
			N).
trie_step_cases([], _, _, _, _, _, _, A, A).
trie_step_cases([A|I], B, C, D, E, F, G, H, K) :-
	trie_step_case(A,
		       B,
		       C,
		       D,
		       E,
		       F,
		       G,
		       H,
		       J),
	trie_step_cases(I,
			B,
			C,
			D,
			E,
			F,
			G,
			J,
			K).
trie_step_case(H/I, B, P, S, Q, D, B1, [A|M], N) :-
	A= (C:-O),
	E is B+1,
	functor(C, D, E),
	arg(1, C, G),
	C=..[_, _|F],
	once(append(X, [L], F)),
	functor(G, H, I),
	G=..[_|J],
	append(J, F, K),
	(   K==[L]
	->  M=N,
	    O=true,
	    rec_cases(P,
		      _,
		      Q,
		      H/I,
		      _,
		      _,
		      R),
	    R=[L]
	;   rec_cases(P,
		      S,
		      Q,
		      H/I,
		      U,
		      T,
		      R),
	    (   T=[V]
	    ->  M=N,
		O=true,
		append([U, V, R], K)
	    ;   pairup(U, T, W),
		common_pattern(W, A1, C1, F1),
		append(J, X, [Y|Z]),
		Y-Z=A1,
		fresh_symbol(B1, D1),
		append(C1, [L], E1),
		O=..[D1|E1],
		maplist(head_tail, F1, G1, H1),
		trie_step(G1,
			  D1,
			  B1,
			  H1,
			  R,
			  M,
			  N)
	    )
	).
head_tail([A|B], A, B).
rec_cases([], [], [], _, [], [], []).
rec_cases([A|L], [E|M], [K|N], B/C, F, H, J) :-
	(   functor(A, B, C),
	    A=..[_|D],
	    append(D, E, [G|I])
	->  F=[G|O],
	    H=[I|P],
	    J=[K|Q],
	    rec_cases(L,
		      M,
		      N,
		      B/C,
		      O,
		      P,
		      Q)
	;   rec_cases(L,
		      M,
		      N,
		      B/C,
		      F,
		      H,
		      J)
	).
common_pattern(A, B, C, D) :-
	fold1(chr_translate:gct, A, B),
	term_variables(B, C),
	findall(C, member(B, A), D).
gct(A, B, C) :-
	gct_(A, B, C, [], _).
gct_(A, B, G, K, L) :-
	(   nonvar(A),
	    nonvar(B),
	    functor(A, C, E),
	    functor(B, D, F),
	    C==D,
	    E==F
	->  functor(G, C, E),
	    A=..[_|H],
	    B=..[_|I],
	    G=..[_|J],
	    maplist_dcg(chr_translate:gct_,
			H,
			I,
			J,
			K,
			L)
	;   lookup_eq(K, A+B, G)
	->  L=K
	;   L=[A+B-G|K]
	).
