normalize_heap(A, B, I) :-
	(   find_min_q(A, _-B)
	->  delete_min_q(A,
			 F,
			 tuple(D, C, _)-_),
	    (   C>1
	    ->  E is C-1,
		get_occurrence(D, E, G, _),
		insert_q(F,
			 tuple(D, E, G)- (-G),
			 H)
	    ;   H=F
	    ),
	    normalize_heap(H, B, I)
	;   I=A
	).
head_types_modes_condition([], _, true).
head_types_modes_condition([B|D], A, (C, E)) :-
