	    replace_some_heads([],
			       D,
			       E,
			       F,
			       [],
			       G,
			       H,
			       I,
			       S),
	    append(Q, S, J)
	;   C=M,
	    replace_some_heads([],
			       D,
			       E,
			       [B|F],
			       [],
			       G,
			       H,
			       I,
			       J)
	).
replace_some_heads([O|D], E, [A|F], [B|G], [C|H], I, J, K, L) :- !,
	(   A==B
	->  C=B,
	    replace_some_heads(D,
			       E,
			       F,
			       G,
			       H,
			       I,
			       J,
			       K,
			       L)
	;   B=functor(M, P, N),
	    A==M
	->  length(Q, N),
	    (   var(O)
	    ->  S=[],
		C=..[P|Q]
	    ;   O=..[P|R],
		use_same_args(R,
			      Q,
			      T,
			      J,
			      K,
			      S),
		C=..[P|T]
	    ),
	    replace_some_heads(D,
			       E,
			       F,
			       G,
			       H,
			       I,
			       J,
			       K,
			       U),
	    append(S, U, L)
	;   C=O,
	    replace_some_heads(D,
			       E,
			       F,
			       [B|G],
			       H,
			       I,
			       J,
			       K,
			       L)
