	    sort_by_key([T|Y], [B1|M], C1),
	    F1='chr debug_event'(try(C1, [], D1, E1)),
	    G1='chr debug_event'(apply(C1, [], D1, E1)),
	    instrument_goal(I1, F1, G1, H1)
	;   H1=I1
	),
	actual_cut(C, S, I1),
	P1= (J1:-K1, L1, H1, M1, N1, O1),
	add_location(P1, I, R1),
	Q1=[R1|S1].
actual_cut(A, B, C) :-
