ast_head_arg_matches_([A-G|L], [D|M], C, E, F, B1, C1) :-
	(   A=var(B, _)
	->  (   get_assoc(B, C, H)
	    ->  (   D= (+)
		->  (   tree_set_memberchk(B, E)
		    ->  F=[G=H|I],
			J=E
		    ;   F=[G==H|I],
			tree_set_add(E, B, J)
		    )
		;   F=[G==H|I],
		    J=E
		),
		K=C
	    ;   put_assoc(B, C, G, K),
		F=I,
		(   D= (+)
		->  tree_set_add(E, B, J)
		;   J=E
		)
	    ),
	    Q=L,
	    R=M
	;   ground(A),
	    A='$chr_identifier_match'(O, N)
	->  identifier_label_atom(N, G, O, P),
	    F=[P|I],
	    C=K,
	    J=E,
	    Q=L,
	    R=M
	;   A=atomic(S)
	->  (   D= (+)
	    ->  F=[G=S|I]
	    ;   F=[G==S|I]
	    ),
	    C=K,
	    J=E,
	    Q=L,
	    R=M
	;   D== (+),
	    ast_is_ground(E, A)
	->  ast_instantiate(C, A, T),
	    F=[G=T|I],
	    C=K,
	    J=E,
	    Q=L,
	    R=M
	;   D== (?),
	    ast_is_ground(E, A)
	->  ast_instantiate(C, A, T),
	    F=[G==T|I],
	    C=K,
	    J=E,
	    Q=L,
	    R=M
	;   A=compound(U, V, X, _),
	    functor(W, U, V),
	    W=..[_|Y],
	    (   D= (+)
	    ->  F=[G=W|I]
	    ;   F=[nonvar(G), G=W|I]
	    ),
	    pairup(X, Y, Z),
	    append(Z, L, Q),
	    replicate(_, D, A1),
	    append(A1, M, R),
	    K=C,
	    J=E
	),
	ast_head_arg_matches_(Q,
			      R,
			      K,
			      J,
			      I,
			      B1,
			      C1).
